<?phpnamespace common\components\helpers;use Yii;use yii\base\Model;class ExtendedModel extends Model{    /**     * @var array validation errors (attribute name => array of errors)     */    private $_errors;    /**     * @return bool||string     */    public function errors()    {        foreach ($this->getErrors() as $error) {            return $error[0];        }        return false;    }}